        })

        //console.log(questionListCount.indexOf)
        //console.log(this.index)
    }

    const QuestionList = ()=>{
        return (
            <ul id="questionList">
                <li onClick={ ChangeCategory(food) }>음식</li>
                <li onClick={ ChangeCategory(hotel) }>호텔</li>
                <li onClick={ ChangeCategory(local) }>관광지</li>
            </ul>
        )
    }
    const ExplanationImg = ()=>{
        const listItem = []
        for(let i=0; i<4; i++){
            listItem.push(
                <div className="imgBox">
                    <p className="imgTitle"># 스시</p>
                    <div className="darken"></div>
                    <img src="images/osaka_man/food/Takoyasu.jpg"/>
                    <div><a href="https://www.naver.com/"></a></div>
                </div>
            );
        };
        return listItem;
    }

    if (detailContent !== "" && detailTitle !== "") {
        console.log(detailContent);
    }

    return (
        <div className="Explanation">
            <h3>{detailTitle}</h3>
            <p>{detailContent}</p>
            {QuestionList()}
            <div className="questionImg">
                {ExplanationImg()}
            </div>
            {children}
        </div>
    );
};
export default Explanation;
