import './scss/Explanation.scss';
//import './scss/Common.scss'
// import { useState, useEffect } from 'react';


const readDB = () => {
  const xhttp = new XMLHttpRequest();
  xhttp.onreadystatechange = () => {
      if (xhttp.readyState == 4 && xhttp.status == 200) {
          const resList = JSON.parse(xhttp.responseText).res_list;
          return (
            <p>{resList}</p>
          )
      }
  };
  xhttp.open("POST", `http://kkms4001.iptime.org:10093/question_list/`, true);
  xhttp.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
  xhttp.send();
};

const Explanation = ({children, detailTitle, detailContent}) => {
    if (detailContent !== "" && detailTitle !== "") {
        console.log(detailContent);
    }
    readDB()
    return (
        <div className="Explanation">
            <h3>{detailTitle}</h3>
            <p>{detailContent}</p>
            {children}

        </div>
    );
};
export default Explanation;
